import React, { useState, useEffect } from 'react';
import { useDispatch } from 'react-redux';
import Button from '~/component/Button';
import createPeriods from '~/util/createPeriod';
import { getAnnimeAsync } from '../ducks/anime/slice';

const date = new Date();

const Anime = () => {
	const dispatch = useDispatch();

	const buttonList = [];

	const seasons = ['冬', '秋', '夏', '春'];

	const [season, setSeason] = useState({
		year: date.getFullYear(),
		period: createPeriods(date.getMonth() + 1),
	});

	const getDefaultAnimeList = () => {
		dispatch(getAnnimeAsync(2020, 4));
	};

	for (let i = date.getFullYear(); 2014 <= i; i--) {
		seasons.forEach((element) => {
			buttonList.push(
				<Button
					year={i}
					season={element}
					onClick={() => {
						setSeason({ ...season, year: i, period: 1 });
					}}
				></Button>,
			);
		});
	}

	useEffect(() => {
		dispatch(getAnnimeAsync(anime.year, anime.period));
	});

	return (
		<>
			<div onLoad={getDefaultAnimeList}>{buttonList}</div>
		</>
	);
};

export default Anime;
