import React, { Component, useEffect, useState } from 'react';

const Button = (props) => {
	const [season, setSeason] = useState();

	useEffect(() => {
		const season = () => {
			switch (props.period) {
				case 1:
					'春';
				case 2:
					'夏';
				case 3:
					'秋';
				case 4:
					'冬';
			}
		};
	});

	return (
		<>
			<button type="button" onLoad={season}>{`${props.year} ${props.period}`}</button>
			<br></br>
		</>
	);
};

export default Button;
