import React, { useState, useEffect } from 'react';
import { useDispatch } from 'react-redux';
import Button from '~/component/Button';
import { getDefaultAnnimeAsync } from '../ducks/anime/slice';
import { useAnimeState } from '~/ducks/anime/selectors';

const date = new Date();

const Anime = () => {
	const dispatch = useDispatch();
	const animeList = useAnimeState;

	const getDefaultAnimeList = () => {
		dispatch(getDefaultAnnimeAsync(2020, 4));
	};

	const [anime, setAnime] = useState({
		anime: useAnimeState,
	});

	useEffect(() => {
		dispatch(getDefaultAnnimeAsync(2020, 4));
	});

	return (onload = getDefaultAnimeList);
};

export default Anime;
