import React, { useState, useEffect } from 'react';
import { useDispatch } from 'react-redux';
import Button from '~/component/Button';
import { connect } from 'react-redux';
import { getAnnimeAsync } from '../ducks/anime/slice';
import { useAnimeState } from '~/ducks/anime/selectors';

const date = new Date();

const Anime = () => {
	const dispatch = useDispatch();

	const buttonList = [];

	const [anime, setAnime] = useState({
		year: null,
		period: null,
		buttonList: [],
	});

	const getDefaultAnimeList = () => {
		dispatch(getAnnimeAsync(2020, 4));
	};

	useEffect(() => {
		for (let i = date.getFullYear(); 2014 <= i; i--) {
			for (let j = 4; 1 <= j; j--) {
				buttonList.push(<Button year={i} period={j}></Button>);
			}
		}
		setAnime({
			...anime,
			buttonList: buttonList,
		});
		dispatch(getAnnimeAsync(2020, 4));
	});

	return (
		<>
			<div onLoad={getDefaultAnimeList}>{buttonList}</div>
		</>
	);
};

export default Anime;
