import React, { useState, useEffect } from 'react';
import { useDispatch } from 'react-redux';
import Button from '~/component/Button';
import { getDefaultAnnimeAsync, getAnnimeAsync } from '../ducks/anime/slice';
import { useAnimeState } from '~/ducks/anime/selectors';

const date = new Date();

const Anime = () => {
	const dispatch = useDispatch();

	const [anime, setAnime] = useState({
		anime: useAnimeState,
	});

	const getDefaultAnimeList = () => {
		dispatch(getDefaultAnnimeAsync());
	};

	useEffect(() => {
		dispatch(getAnnimeAsync(2020, 3));
	});

	return (
		<>
			<div onLoad={getDefaultAnimeList}></div>
		</>
	);
};

export default Anime;
