import React, { useState, useEffect } from 'react';
import { useDispatch } from 'react-redux';
import Button from '~/component/Button';
import createPeriods from '~/util/createPeriod';
import { getAnnimeAsync } from '../ducks/anime/slice';

const date = new Date();

const Anime = () => {
	const dispatch = useDispatch();

	const buttonList = [];

	const seasons = { 4: '冬', 3: '秋', 2: '夏', 1: '春' };

	const [season, setSeason] = useState({
		year: date.getFullYear(),
		period: createPeriods(date.getMonth() + 1),
	});

	const getDefaultAnimeList = () => {
		dispatch(getAnnimeAsync(2020, 4));
	};

	const handleClickSetSeason = (year: number, peropd: number) => {};

	for (let i = date.getFullYear(); 2014 <= i; i--) {
		Object.keys(seasons).forEach((key) => {
			buttonList.push(<Button year={i} season={seasons[key]} period={key}></Button>);
		});
	}

	useEffect(() => {
		console.log(season);
		dispatch(getAnnimeAsync(season.year, season.period));
	});

	return (
		<>
			<div onLoad={getDefaultAnimeList}>{buttonList}</div>
		</>
	);
};

export default Anime;
