import React, { Component, useEffect, useState } from 'react';

const Button = (props) => {
	const [seasonOfString, setSeason] = useState('aaa');

	useEffect(() => {
		let season = '';
		if (props.period == 1) {
			season = '春';
		} else if (props.period == 2) {
			season = '夏';
		} else if (props.period == 3) {
			season = '秋';
		} else if (props.period == 4) {
			season = '冬';
		}

		setSeason({
			seasonOfString: season,
		});
	});

	return (
		<>
			<button type="button">{`${props.year} ${seasonOfString}`}</button>
			<br></br>
		</>
	);
};

export default Button;
