import { createSlice, PayloadAction } from '@reduxjs/toolkit';
import axios from 'axios';
import { NetworkState, AnimeInfoState } from '../../type/type';

export const initialState: NetworkState = {
	items: {
		id: null,
		title: '',
		title_short1: '',
		title_short2: '',
		title_short3: '',
		public_url: '',
		twitter_account: '',
		twitter_hash_tag: '',
		cours_id: null,
		created_at: '',
		updated_at: '',
		sex: null,
		sequel: null,
		city_code: null,
		city_name: '',
		product_companies: 'null',
	},
	loading: true,
	error: null,
};

const animeInfoSlice = createSlice({
	name: 'anime',
	initialState,
	reducers: {
		getAnnimeRequest: (state, action: PayloadAction<AnimeInfoState>) => ({
			...state,
			items: action.payload,
		}),
		getAnnimeFailure: (state, action) => ({
			...state,
		}),
	},
});

export const { getAnnimeRequest } = animeInfoSlice.actions;

export default animeInfoSlice;

export const getAnnimeAsync = (year: number, season: number) => async (dispatch: (arg0: { payload: any; type: string }) => void) => {
	const response = await axios.get(`http://api.moemoe.tokyo/anime/v1/master/${year}/${season}`).then((response) => {
		dispatch(getAnnimeRequest(response.data));
	});
	console.log(response);
};
