import React, { useState, useEffect } from 'react';
import { useDispatch } from 'react-redux';
import Button from '~/component/Button';
import { getAnnimeAsync } from '../ducks/anime/slice';

const date = new Date();

const Anime = () => {
	const dispatch = useDispatch();

	const buttonList = [];

	const [anime, setAnime] = useState({
		year: date.getFullYear(),
		period: date.getMonth() + 1,
	});

	const getDefaultAnimeList = () => {
		dispatch(getAnnimeAsync(2020, 4));
	};

	for (let i = date.getFullYear(); 2014 <= i; i--) {
		for (let j = 4; 1 <= j; j--) {
			buttonList.push(<Button year={i} period={j}></Button>);
		}
	}

	useEffect(() => {
		dispatch(getAnnimeAsync(anime.year, anime.period));
	});

	return (
		<>
			<div onLoad={getDefaultAnimeList}>{buttonList}</div>
		</>
	);
};

export default Anime;
