import React, { Component, useEffect, useState } from 'react';

const Button = (props) => {
	const [season, setSeason] = useState('null');

	useEffect(() => {
		const season = (): string => {
			switch (props.period) {
				case 1:
					return '春';
				case 2:
					return;
					break;
				case 3:
					return;
					break;
				case 4:
					return;
					break;
			}
		};
		setSeason({
			season: season,
		});
	});

	return (
		<>
			<button type="button" onLoad={season}>{`${props.year} ${props.period}`}</button>
			<br></br>
		</>
	);
};

export default Button;
