import React, { Component, useEffect, useState } from 'react';

const Button = (props) => {
	const [season, setSeason] = useState();

	useEffect(() => {
		switch (props.period) {
			case 1:
				return '春';
			case 2:
				return '夏';
			case 3:
				return '秋';
			case 4:
				return '冬';
		}
	});

	return (
		<>
			<button type="button" onLoad={season}>{`${props.year} ${props.period}`}</button>
			<br></br>
		</>
	);
};

export default Button;
