import React, { Component, useEffect, useState } from 'react';

const Button = (props) => {
	const [season, setSeason] = useState('null');

	useEffect(() => {
		const season = '';
		if (this.props == 1) {
			season = '春';
		}
		setSeason({
			season: season,
		});
	});

	return (
		<>
			<button type="button" onLoad={season}>{`${props.year} ${props.period}`}</button>
			<br></br>
		</>
	);
};

export default Button;
