import { createSlice, PayloadAction } from '@reduxjs/toolkit';
import axios from 'axios';
import createPeriod from '~/util/createPeriod';
import { NetworkState, SeasonState, AnimeInfoState } from '../../type/type';

const date = new Date();

export const initialState: NetworkState = {
	items: [
		{
			year: null,
			period: null,
			animeList: getDefaultAnnimeAsync(2020, 2),
		},
	],
	loading: true,
	error: null,
};

const animeInfoSlice = createSlice({
	name: 'anime',
	initialState,
	reducers: {
		getDefaultAnnimeRequest: (state, action: PayloadAction<Array<SeasonState>>) => ({
			...state,
			items: action.payload,
		}),
		getAnnimeRequest(state, action: PayloadAction<SeasonState>) {
			state.items.push(action.payload);
		},
		getAnnimeFailure: (state, action) => ({
			...state,
		}),
	},
});

export const { getDefaultAnnimeRequest, getAnnimeRequest } = animeInfoSlice.actions;

export default animeInfoSlice;

export const getDefaultAnnimeAsync = (year: number, season: number) => async (dispatch: (arg0: { payload: any; type: string }) => void) => {
	const period = createPeriod(date.getMonth() + 1);
	await axios.get(`http://api.moemoe.tokyo/anime/v1/master/${date.getFullYear()}/${period}`).then((response) => {
		const items: Array<SeasonState> = [{ year: date.getFullYear(), period: period, animeList: response.data }];
		dispatch(getDefaultAnnimeRequest(items));
	});
};
